export default function SyllableCount (props) {

const regex = /[a-zA-Z]+\b/gm;
let m
let array = []
while ((m = regex.exec(props)) !== null) {
    if (m.index === regex.lastIndex) {
        regex.lastIndex++;
    }
m.forEach((match, groupIndex) => {
    fetch(`https://api.datamuse.com/words?sp=${match}&md=s&max=1`, {
    "method": "GET"
    })
    .then(response => response.json())
    .then(data => {
        let completeArray = array.push(parseInt(data[0].numSyllables))
        return console.log(completeArray)
    })
    .catch(err => {
        return console.log(err)
    });
});
}
}
