import React, {useState} from "react"
import './styles.css'
import EnglishForm from "./EnglishForm"
import FreeformForm from "./FreeformForm"


export default function NewPost() {
    const [radio, setRadio] = useState("5-7-5")

    let input
    if (radio === "5-7-5") {
        input  = <EnglishForm />
    } else if (radio === "Freeform") {
        input = <FreeformForm />
    }

    return(
        <div>
            <div className="form-group border border-primary rounded">
                <label htmlFor="5-7-5">5-7-5:</label>
                <input className="form-check"
                        name="5-7-5"
                        type="radio"
                        checked={radio === "5-7-5"}
                        value="5-7-5"
                        onChange={function(event) {setRadio(event.target.value)}}></input>
                <br/>
                <label htmlFor="Freeform">Freeform:</label>
                <input className="form-check"
                        name="Freeform"
                        type="radio"
                        checked={radio === "Freeform"}
                        value="Freeform"
                        onChange={function(event) {setRadio(event.target.value)}}></input>
                <br/>
                {input}
            </div>
        </div>
    )
}
