import React, { useState } from "react";
import { useSpring, animated } from "react-spring";
import './styles.css'

//Registration form with Bounce on submit logic.
function RegisterForm() {
    const [loc, setLoc] = useState(false);
    const [formData, setData] = useState({
      username: '',
      email: '',
      password: '',
      matchword: '',
    })

  const bounceOut = useSpring({
      from: { opacity: 1,
              transform: 'translateY(0%)'
          },
      to: {opacity: loc ? 0 : 1,
          transform: loc ? 'translateY(-500px)' : 'translateY(0%)'}
  });

  function handleSubmit (event) {
    return 1;
  }

  function handleChange (event) {
    const {name, value} = event.target
    setdat({...values, [name]: value})
  }
 
  return(
        <animated.div  style={bounceOut}>
          <div className="form-group border border-primary rounded">
            <form onSubmit={handleSubmit}>
              <label htmlFor="userName">Username: </label>
              <input autoComplete="off"
                    className="form-control"
                    type="text"
                    name="userName"
                    value={formData.username}
                    onChange={handleChange} />
              <label htmlFor="email">Email: </label>
              <input autoComplete="off"
                    className="form-control"
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}></input>
              <label htmlFor="passWord">Password: </label>
              <input autoComplete="off"
                    className="form-control"
                    type="password"
                    name="passWord"
                    value={formData.password}
                    onChange={handleChange}></input>
              <label htmlFor="matchWord">Repeat Password: </label>
              <input autoComplete="off"
                    className="form-control"
                    type="password"
                    name="matchWord"
                    value={formData.matchword}
                    onChange={handleChange}></input>
              <br></br>
              <button className="btn btn-primary" onClick={() => setLoc(anything => !anything)}>Register</button>
            </form>
          </div>
        </animated.div> 
    )

}

export default RegisterForm
