 import React from 'react'

export default function SyllableCount (props) {

console.log(props)
const regex = /[a-zA-Z]+\b/gm;
let str = props.string;
let m
let array = []
let corn;
while ((m = regex.exec(str)) !== null) {
    if (m.index === regex.lastIndex) {
        regex.lastIndex++;
    }
m.forEach((match, groupIndex) => {
    fetch(`https://api.datamuse.com/words?sp=${match}&md=s&max=1`, {
    "method": "GET"
    })
    .then(response => response.json())
    .then(data => {
        array.push(parseInt(data[0].numSyllables))
    })
    .catch(err => {
        return (
            <div>Error</div>
        )
    });
});
}
}
